var ROOT_PATH = module.exports = {};

ROOT_PATH.pwd = process.cwd();

ROOT_PATH.init = function () {

	var arrPwd = __dirname.split('/');
	var pathTemp = '';
	var nodeModulesIndex = [];
	var arrRoot = [];
	var root = '';

	for ( var i = 0, len = arrPwd.length; i < len; i++ ) {
		if ( arrPwd[i] === 'node_modules' ) {
			nodeModulesIndex.push(i);
		}
	}

	arrRoot = arrPwd.splice( 0, nodeModulesIndex[0] );

	for ( var i = 0, len = arrRoot.length; i < len; i++ ) {
		root += arrRoot[i] + '/';
	}

	root = root.substring(0, root.length - 1);

	return root;
};